import React, { Component } from 'react';
import TodoListView from '../views/TodoListView';
import {inject,observer} from 'mobx-react';
import autobind from "autobind-decorator";


@inject('todoStore')
@autobind
@observer
class TodoListContainer extends Component {
  selTodo(todo){
    this.props.todoStore.selTodo(todo);
  }
  render(){
    let {todoStore,searchText}=this.props;

    todoStore.todos = todoStore.todos.filter((todo) =>todo.title.toLowerCase().indexOf(searchText.toLowerCase()))
    return (

      <TodoListView todos={todoStore.todos}
                    selTodo={this.selTodo}/>
    )
  }
}

export default TodoListContainer;
