import {observable, action, computed, makeObservable,toJS} from 'mobx';

class TodoStore{

    constructor(){
        makeObservable(this);
    }

    @observable
    _todo={
    }//id,title,date

    get todo(){
        return this._todo;
    }
    @observable
    _todos=[];

    @computed
    get todos(){
      //  return this._todos?this._todos.slice():[];
        return toJS(this._todos);
    }
    @action
    onSetTodo(name,value){
        this._todo={
            ...this._todo,
            [name] : value,
        }
    }
    @action
    addTodos(todo){
        this._todos.push(todo);
    }
    @action
    selTodo(todo){
        this._todo=todo;
    }
    @action
    updateTodo(todo){
        let foundTodo =this._todos
    }
}

export default new TodoStore();
