import {observable, action, computed, makeObservable} from 'mobx';

class TodoStore{

    constructor(){
        makeObservable(this);
    }

    @observable
    _todo={
    }//id,title,date

    get todo(){
        return this._todo;
    }
    @observable
    _todos=[];

    get todos(){
        return this._todos;
    }
    @action
    onSetTodo(name,value){
        this._todo={
            ...this._todo,
            [name] : value,
        }
    }
    addTodos(){ }
}

export default new TodoStore();
